    @Override
	public <V extends ExternalModelView> ValidationResult validate(Object fieldValue, V view, String fieldName, SecurityFilter filter, SerializableField field, Map<String, Object> validationContext) {
		if (fieldValue == null) {
			return ValidationResult.pass();
		}
		
		String address = (String)fieldValue;
		
		if (EMAIL_REGEX.matcher(address).matches()) {
			return ValidationResult.pass();
		} else {
			return ValidationResult.fail(EmailValidatorFail, fieldName, address);
		}
	}
	
	public boolean validEmail(String address){
		return EMAIL_REGEX.matcher(address).matches();
	}

